    document.addEventListener('DOMContentLoaded', () => {
      const form = document.getElementById('memberForm');
      const status = document.getElementById('memberStatus');

      form.addEventListener('submit', async (e) => {
        e.preventDefault();

        // Collect interests (checkbox group)
        const interests = Array.from(form.querySelectorAll('input[name="interests"]:checked')).map(i => i.value);

        // Build payload
        const payload = {
          fullName: form.fullName.value.trim(),
          email: form.email.value.trim(),
          phone: form.phone.value.trim(),
          countryCity: form.countryCity.value.trim(),
          dob: form.dob.value || null,
          gender: form.gender.value || null,
          organization: form.organization.value.trim(),
          role: form.role.value.trim(),
          availability: form.availability.value,
          interests: interests,
          motivation: form.motivation.value.trim(),
          createdAt: new Date().toISOString()
        };

        status.style.color = 'green';
        status.textContent = 'Envoi en cours...';

        try {
          const res = await fetch('/.netlify/functions/saveMember', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(payload)
          });

          const json = await res.json();

          if (res.ok && json.result === 'success') {
            status.style.color = 'green';
            status.textContent = 'Votre adh\xE9sion a \xE9t\xE9 envoy\xE9e. Merci !';
            form.reset();
            // scroll to top of form to show status
            form.scrollIntoView({ behavior: 'smooth', block: 'start' });
          } else {
            status.style.color = 'red';
            status.textContent = 'Erreur lors de l\u2019envoi : ' + (json.error || res.statusText);
          }
        } catch (err) {
          status.style.color = 'red';
          status.textContent = 'Erreur r\xE9seau : ' + err.message;
        }
      });
    });
